//Chuong trinh xac dinh tam giac nay la gi:#include <stdio.h>int main(){	//Nhap 3 canh cua tam giac:    float a,b,c;    printf("Nhap canh thu nhat: ");    scanf("%f",&a);    printf("Nhap canh thu hai: ");    scanf("%f",&b);    printf("Nhap canh thu ba: ");    scanf("%f",&c);    //Xac dinh tam giac:    //Neu la tam giac can (2 canh bat ky bang nhau):    if ((a==b) || (b==c) || (a==c))    printf("Tam giac can !");    //Neu la tam giac deu (3 canh bang nhau):    else if (a==b==c)    	printf("Tam giac deu !");    	//Neu la tam giac vuong (su dung dinh ly Py-ta-go):    	else if ((a*a+b*b==c*c) || (b*b+c*c==a*a) || (a*a+c*c==b*b))    		printf("Tam giac vuong !");    //Neu khong phai 1 trong 3 tam giac tren:    else    printf("Tam giac thuong !");}